import { ScatterplotLayer, HexagonLayer } from "deck.gl";

const scatterColor = {
    "通常":[23, 190, 187],
    "制限":[238, 198, 67],
    "停止":[214, 34, 70],
    "設置なし":[192, 192, 192],
    "未回答":[137, 137, 137],
}


export default props => {
  const { data, layerType,  hospitalState} = props;
  console.log(data)

  if (!data) return;

  const scatter = new ScatterplotLayer({
    id: "scatterplot-layer",
    data,
    pickable: true,
    opacity: 0.8,
    stroked: true,
    filled: true,
    radiusScale: 1,
    getPosition: d => [d["経度"],d["緯度"]],
    getRadius: 1000,
    getFillColor: d => scatterColor[d["医療区分回答"]],
    getLineColor: d => scatterColor[d["医療区分回答"]],
    onHover: ({ object, x, y }) => {
      console.log(object);
    }
  });

  const hex = new HexagonLayer({
    id: "hexagon-layer",
    data,
    pickable: true,
    extruded: true,
    radius: 200,
    elevationScale: 4,
    getPosition: d => d.COORDINATES,
    onHover: ({ object, x, y }) => {
      console.log(object);
    }
  });

  const layers = [scatter];

  return layers;
};
