import { red } from '@material-ui/core/colors';
import { createMuiTheme } from '@material-ui/core/styles';


const theme = createMuiTheme({
    props: {
      MuiButton: {
        size: 'small',
      },
      MuiFilledInput: {
        margin: 'dense',
      },
      MuiFormControl: {
        margin: 'small',
      },
      MuiFormHelperText: {
        margin: 'small',
      },
      MuiIconButton: {
        size: 'small',
      },
      MuiInputBase: {
        margin: 'small',
      },
      MuiInputLabel: {
        fontSize:"12px",
        margin: 'small',
      },
      MuiListItem: {
        dense: true,
      },
      MuiOutlinedInput: {
        margin: 'dense',
      },
      MuiFab: {
        size: 'small',
      },
      MuiTable: {
        size: 'small',
      },
      MuiTextField: {
        margin: 'dense',
      },
      MuiToolbar: {
        variant: 'dense',
      },
    },
    overrides: {
        MuiInputLabel: {
        sizeSmall: {
          // Adjust spacing to reach minimal touch target hitbox
          marginLeft: 10,
          marginRight: 0,
          padding: 6,
        },
      },
    },
  });

export default theme;
