import { ScatterplotLayer, HexagonLayer } from "deck.gl";

const scatterColor = {
    "通常":[23, 190, 187],
    "制限":[238, 198, 67],
    "停止":[214, 34, 70],
    "設置なし":[192, 192, 192],
    "未回答":[137, 137, 137],
}

const colorRange = [
    [1, 152, 189],
    [73, 227, 206],
    [216, 254, 181],
    [254, 237, 177],
    [254, 173, 84],
    [209, 55, 78]
  ];

export default props => {
  const { data, layerType,  hospitalState} = props;
  //console.log(data)

  if (!data) return;

  const scatter = new ScatterplotLayer({
    id: "scatterplot-layer",
    data,
    pickable: true,
    opacity: 0.5,
    stroked: true,
    filled: true,
    radiusScale: 1,
    radiusMinPixels: 6,
    lineWidthMinPixels: 1,
    getPosition: d => [d["経度"],d["緯度"]],
    getRadius: 0,
    getFillColor: d => scatterColor[d["医療区分回答"]],
    getLineColor: d => scatterColor[d["医療区分回答"]],
    onHover: ({ object, x, y }) => {
      console.log(object);
    }
  });

  const hex = new HexagonLayer({
    id: "hexagon-layer",
    data,
    colorRange,
    opacity: 0.5,
    radius: 10000,
    extruded: true,
    elevationScale: 40,
    elevationRange: [0, 3000],
    getPosition: d => [d["経度"],d["緯度"]],
  })

  const layers = [scatter, hex];

  return layers;
};
