import React, { useEffect, useState ,useRef } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';

import MapGL from "react-map-gl";

import ControllPanel from './ControllPanel';


//mapboxのトークンは自分で取得したものに変更してください。
//このトークンは突然使えなくなる可能性があります。
const MAPBOX_TOKEN =
  "pk.eyJ1Ijoic2hpbWl6dSIsImEiOiJjam95MDBhamYxMjA1M2tyemk2aHMwenp5In0.i2kMIJulhyPLwp3jiLlpsA";


const useHeaderStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


const useMapStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    height:"100%",
    backgroundColor:"black"
  },
}));

function App() {
  const headerClasses = useHeaderStyles();
  const mapClasses = useMapStyles();
  const ref = useRef(null); //コンポーネントのwidth,height取得用


  const [viewport, setViewport] = useState 
  ({
    width: 0,
    height: 0,
    longitude: 139.0110974,
    latitude: 36.3226885,
    zoom: 6,
    maxZoom: 20,
  })

	//ロード時にコンポネーントのwidth,heightを取得
	useEffect(() => {
		const width = ref.current ? ref.current.offsetWidth : 0;
		const height = ref.current ? ref.current.offsetHeight : 0;
    console.log(width, height)

		setViewport({
        width: width,
        height: height,
        longitude: 139.5449663,
        latitude: 35.7185268,
        zoom: 8,
        maxZoom: 16
      })

	}, []);


  return (
    <>
    <div className={headerClasses.root}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" className={headerClasses.title}>
          医療提供体制の状況
          </Typography>
        </Toolbar>
      </AppBar>
    </div>
    <div ref={ref} className={mapClasses.root}>
    <MapGL
        {...viewport}
        mapStyle={"mapbox://styles/mapbox/streets-v11"}
        mapboxApiAccessToken={MAPBOX_TOKEN}
        onViewportChange={v => setViewport(v)}
      />
    </div>

    </>

  )
}

export default App;
