import React, { useEffect, useState, useRef } from "react";
import AppBar from "@material-ui/core/AppBar";

import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";

import MapGL from "react-map-gl";

import CheckBoxs from "./components/CheckBoxs";
import BottomBtn from "./components/BottomBtn";


import { csv as d3csv } from 'd3-fetch';

//mapboxのトークンは自分で取得したものに変更してください。
//このトークンは突然使えなくなる可能性があります。
const MAPBOX_TOKEN =
  "pk.eyJ1Ijoic2hpbWl6dSIsImEiOiJjam95MDBhamYxMjA1M2tyemk2aHMwenp5In0.i2kMIJulhyPLwp3jiLlpsA";

  const DATA_URL = "data/covid-19_daily_survey.csv";



//data convert fnction
const  csvcast = (d, i) => {
	d.id = d.code;
	Object.keys(d).forEach((key) => {
		if (!isNaN(+d[key]) && key !== 'id') d[key] = +d[key];
	});

	d['type'] = typelist[d["L01_047"]] || d["L01_047"];
	return d;
};


function App() {
  const mapRef = useRef(null);
  const bbtnRef = useRef(null);
  const [data, setData] = useState(null);

  const [viewport, setViewport] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
    longitude: 139.0110974,
    latitude: 36.3226885,
    zoom: 6,
    maxZoom: 20
  });

  const handleChangeCheckbox = v => {
    console.log(v);
  };

  const handleChangeBottomBtn = v => {
    console.log(v);
  };

  const getMapsize = () => {
    const width = mapRef.current ? mapRef.current.offsetWidth : 0;
    const height = mapRef.current ? mapRef.current.offsetHeight : 0;
    const bbtnHeight = bbtnRef.current ? bbtnRef.current.offsetHeight : 0;

    setViewport(v => {
      return {
        ...v,
        width: width,
        height: height - bbtnHeight
      };
    });
  };

  //ロード時にコンポネーントのwidth,heightを取得
  useEffect(getMapsize, []);

  //データロード
  useEffect(()=>{
		const fetchValueData = async (url) => {
      const result = await d3csv(DATA_URL, csvcast);
      console.log(result)
			setData(result);
		};
		fetchValueData();

  }, []);


  //resize
  useEffect(() => {
    window.addEventListener("resize", getMapsize);
    return () => window.removeEventListener("resize", getMapsize);
  }, []);

  return (
    <>
      <div id="header">
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6">医療提供体制の状況</Typography>
          </Toolbar>
        </AppBar>
        <CheckBoxs changeCheckbox={handleChangeCheckbox} />
      </div>
      <div id="panel"></div>
      <div id="map" ref={mapRef}>
        <MapGL
          style={{}}
          {...viewport}
          mapStyle={"mapbox://styles/mapbox/streets-v11"}
          mapboxApiAccessToken={MAPBOX_TOKEN}
          onViewportChange={v => setViewport(v)}
        />
      </div>

      <div id="bottom" ref={bbtnRef}>
        <BottomBtn changeBottomBtn={handleChangeBottomBtn} />
      </div>
    </>
  );
}

export default App;

