import React, { useEffect, useState ,useRef } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';

import MapGL from "react-map-gl";

import ControllPanel from './ControllPanel';


//Maptilerプラグインの読み込み
try {
  const mapboxgl = require("mapbox-gl");
  mapboxgl.setRTLTextPlugin(
    "https://cdn.maptiler.com/mapbox-gl-js/plugins/mapbox-gl-rtl-text/v0.1.2/mapbox-gl-rtl-text.js"
  );
} catch (e) {
  console.error(e);
}



const useHeaderStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


const useMapStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    height:"100%",
    backgroundColor:"black"
  },
}));

function App() {
  const headerClasses = useHeaderStyles();
  const mapClasses = useMapStyles();
  const ref = useRef(null); //コンポーネントのwidth,height取得用


  const [viewport, setViewport] = useState(null);  
  setViewport({
    width: 900,
    height: 600,
    longitude: 139.0110974,
    latitude: 36.3226885,
    zoom: 16,
    maxZoom: 20,
    pitch: 65,
    bearing: 220
  })


	//ロード時にコンポネーントのwidth,heightを取得
	useEffect(() => {
		const width = ref.current ? ref.current.offsetWidth : 0;
		const height = ref.current ? ref.current.offsetHeight : 0;

		setViewport({
			width: 900,
			height: 600,
      longitude: 139.0110974,
      latitude: 36.3226885,
      zoom: 16,
      maxZoom: 20,
      pitch: 65,
      bearing: 220
		})

	}, []);

  return (
    <>

    <div className={headerClasses.root}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" className={headerClasses.title}>
          医療提供体制の状況
          </Typography>
        </Toolbar>
      </AppBar>
    </div>
    <div ref={ref} className={mapClasses.root}>
    <MapGL
        {...viewport}
        mapStyle={
          //maptilerで作成したstyle.jsonを適用する
          "https://api.maptiler.com/maps/79042832-38a3-421f-89d1-723b4c07bf00/style.json?key=d7rAFWe6KEnmDwNQLVD9"
        }
        onViewportChange={v => setViewport(v)}
      />
    </div>

    </>

  )
}

export default App;
