    unanswered :"未回答",
}

const scatterColor = {
    "通常":[23, 190, 187],
    "制限":[238, 198, 67],
    "停止":[214, 34, 70],
    "設置なし":[192, 192, 192],
    "未回答":[137, 137, 137],
}

const colorRange = [
    [1, 152, 189],
    [73, 227, 206],
    [216, 254, 181],
    [254, 237, 177],
    [254, 173, 84],
    [209, 55, 78]
  ];

export default props => {
  const { data, layerType,  hospitalState} = props;

  const statefilter = Object.keys(hospitalState)
    .filter(key => hospitalState[key])
    .map(key => convertStats[key]);


