    filled: true,
    radiusScale: 1,
    radiusMinPixels: 6,
    lineWidthMinPixels: 1,
    getPosition: d => [d["経度"],d["緯度"]],
    getRadius: 0,
    getFillColor: d => scatterColor[d["医療区分回答"]],
    getLineColor: d => scatterColor[d["医療区分回答"]],
    onHover: ({ object, x, y }) => {
      console.log(object);
    }
  });

  const hex = new HexagonLayer({
    id: "hexagon-layer",
    data,
    colorRange,
    opacity: 0.8,
