import { red } from '@material-ui/core/colors';
import { createMuiTheme } from '@material-ui/core/styles';


export const theme = createMuiTheme({
    typography: {
      display1: {
        fontSize: '5em',
      },
      body1: {
        fontSize: '2em',
        color: 'rgba(0,255,0,1)'
      }
    },
    overrides: {
      MuiSnackbarContent: {
        root: {
          backgroundColor: 'rgba(0,255,255,1)',
        }
      },
    },
  });

export default theme;
