import React from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';

import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';

import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';


const useChardStyles = makeStyles((theme) => ({
  root: {
    backgroundColor:"white",
    '& > *': {
      margin: theme.spacing(1),
      padding:10,
    },
  },
}));

const GreenCheckbox = withStyles({
  root: {
    color: "gray",
    '&$checked': {
      color: "#17bebb",
    },
  },
  checked: {},
})((props) => <Checkbox color="default" {...props} />);

const YellowCheckbox = withStyles({
  root: {
    color: "gray",
    '&$checked': {
      color: "#eec643",
    },
  },
  checked: {},
})((props) => <Checkbox color="default" {...props} />);

const RedCheckbox = withStyles({
  root: {
    color: "gray",
    '&$checked': {
      color: "#d62246",
    },
  },
  checked: {},
})((props) => <Checkbox color="default" {...props} />);

const Gray1Checkbox = withStyles({
  root: {
    color: "gray",
    '&$checked': {
      color: "#c0c0c0",
    },
  },
  checked: {},
})((props) => <Checkbox color="default" {...props} />);

const Gray2Checkbox = withStyles({
  root: {
    color: "gray",
    '&$checked': {
      color: "##898989",
    },
  },
  checked: {},
})((props) => <Checkbox color="default" {...props} />);

function ControllPanel() {
  const cardClasses = useChardStyles();

  const [state, setState] = React.useState({
    checkedA: true,
    checkedB: true,
    checkedC: true,
    checkedD: true,
    checkedE: true,
  });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  return (
    <>

          <ButtonGroup color="primary">
            <Button  variant="contained">ポイント</Button>
            <Button>六角形</Button>
          </ButtonGroup>
          <FormGroup row>
              <FormControlLabel
                control={<GreenCheckbox checked={state.checkedA} onChange={handleChange} name="checkedA" />}
                label="通常"
              />
              <FormControlLabel
                control={<YellowCheckbox checked={state.checkedB} onChange={handleChange} name="checkedB"  />}
                label="制限"
              />
              <FormControlLabel
                control={<RedCheckbox checked={state.checkedC} onChange={handleChange} name="checkedC" />}
                label="停止"
              />
              <FormControlLabel
                control={<Gray1Checkbox checked={state.checkedD} onChange={handleChange} name="checkedD" />}
                label="設置なし"
              />              
              <FormControlLabel
                control={<Gray2Checkbox checked={state.checkedE} onChange={handleChange} name="checkedE" />}
                label="未回答"
              />              
            </FormGroup>
            </>

  )
}

export default ControllPanel;
