import { ScatterplotLayer, HexagonLayer } from "deck.gl";

export default props => {
  const { data, layerType,  hospitalState} = props;
  console.log(data)

  if (!data) return;

  const scatter = new ScatterplotLayer({
    id: "scatterplot-layer",
    data,
    pickable: true,
    opacity: 0.8,
    stroked: true,
    filled: true,
    radiusScale: 6,
    radiusMinPixels: 1,
    radiusMaxPixels: 100,
    lineWidthMinPixels: 1,
    getPosition: d => [d["経度"],d["緯度"]],
    getRadius: d => Math.sqrt(d.exits),
    getFillColor: d => [255, 140, 0],
    getLineColor: d => [0, 0, 0],
    onHover: ({ object, x, y }) => {
      console.log(object);
    }
  });

  const hex = new HexagonLayer({
    id: "hexagon-layer",
    data,
    pickable: true,
    extruded: true,
    radius: 200,
    elevationScale: 4,
    getPosition: d => d.COORDINATES,
    onHover: ({ object, x, y }) => {
      console.log(object);
    }
  });

  const layers = [scatter];

  return layers;
};
