import React, { useEffect, useState ,useRef } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Container from '@material-ui/core/Container';

import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';

import MapGL from "react-map-gl";

import ControllPanel from './ControllPanel';


//mapboxのトークンは自分で取得したものに変更してください。
//このトークンは突然使えなくなる可能性があります。
const MAPBOX_TOKEN =
  "pk.eyJ1Ijoic2hpbWl6dSIsImEiOiJjam95MDBhamYxMjA1M2tyemk2aHMwenp5In0.i2kMIJulhyPLwp3jiLlpsA";




function App() {


  const ref = useRef(null); //コンポーネントのwidth,height取得用


  const [viewport, setViewport] = useState 
  ({
    width:400,//window.innerWidth,
    height:400,//window.innerHeight,
    longitude: 139.0110974,
    latitude: 36.3226885,
    zoom: 6,
    maxZoom: 20,
  })

	//ロード時にコンポネーントのwidth,heightを取得
	useEffect(() => {
		const width = ref.current ? ref.current.offsetWidth : 0;
    const height = ref.current ? ref.current.offsetHeight : 0;
    console.log(width,height)

  },[])

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6">
          医療提供体制の状況
          </Typography>
        </Toolbar>
      </AppBar>
        <Container maxWidth="sm">
          <ControllPanel  style={{zIndex:99999}}></ControllPanel>
          <MapGL style={{marginTop:-40}}
          {...viewport}
          mapStyle={"mapbox://styles/mapbox/streets-v11"}
          mapboxApiAccessToken={MAPBOX_TOKEN}
          onViewportChange={v => setViewport(v)}
        />  

        </Container>
    </>

  )
}

export default App;

